const repeatString = function(string, num) {
   let i = 0;
   let holder = ""; 
   if(num < 0) {
      return 'ERROR';
  }
   if(!num && num !==0){
   num = number;
}
   while(i < num){
    
        holder += string
        i++
   }
   

   return holder;
};

// Do not edit below this line
module.exports = repeatString;
